import { React, useState } from "react";
import { Switch, Route } from "react-router";
import { getAllAltars } from "../../services/altars";
import { getAllImages } from "../../services/images";
import Collection from "./screens/Collection/Collection.jsx";
import Create from "./screens/Create/Create.jsx";
import About from "../About/About.jsx";

export default function Main(props) {
  const [altars, setAltars] = useState([]);
  const [images, setImages] = useState([]);

  useEffect(() => {
    const fetchAltars = async () => {
      const altarList = await getAllAltars();
      setAltars(altarList);
    };
    fetchAltars();
  }, []);

  useEffect(() => {
    const fetchImages = async () => {
      const imageList = await getAllImages();
      setImages(imageList);
    };
    fetchImages();
  }, []);

  return (
    <div>
      <Route path="/collection">
        <Collection altars={altars}/>
      </Route>
      <Route path="/create">
      {props.currentUser ? <Create user={props.user} /> : <Redirect to="/login" />}
      </Route>
      <Route path="/about">
        <About />
      </Route>
    </div>
  );
}
