import {React, useState} from "react";
import "./AltarForm.css";
import { Switch } from '@headlessui/react'

export default function AltarForm(props) {
const [enabled, setEnabled] = useState(false)


  return (
    <>
    <div className="flex ml-8 mt-32 justify-left w-screen">
      <input
        className="rounded-full py-2 px-7 border border-primary text-primary bg-trp bg-opacity-100 focus:ring-2 focus:ring-white"
        type="text"
        name="email"
        placeholder="altar name"
        // value={formData.email}
        // onChange={handleChange}
      />
    <div className="py-16">
      <Switch
        checked={enabled}
        onChange={setEnabled}
        className={`${enabled ? 'bg-teal-900' : 'bg-teal-700'}
          relative inline-flex flex-shrink-0 h-[38px] w-[74px] border-2 border-transparent rounded-full cursor-pointer transition-colors ease-in-out duration-200 focus:outline-none focus-visible:ring-2  focus-visible:ring-white focus-visible:ring-opacity-75`}
      >
        <span className="sr-only">Use setting</span>
        <span
          aria-hidden="true"
          className={`${enabled ? 'translate-x-9' : 'translate-x-0'}
            pointer-events-none inline-block h-[34px] w-[34px] rounded-full bg-white shadow-lg transform ring-0 transition ease-in-out duration-200`}
        />
      </Switch>
    </div>
    </div>
    </>
  );
}
