import { React, useState } from "react";
import "./AltarForm.css";

export default function AltarForm(props) {
  const [enabled, setEnabled] = useState(false);

  return (
    <div className="flex ml-8 mt-32 justify-left w-screen">
      <input
        className="rounded-full py-2 px-7 border border-primary text-primary bg-trp bg-opacity-100 focus:ring-2 focus:ring-white"
        type="text"
        name="email"
        placeholder="altar name"
        // value={formData.email}
        // onChange={handleChange}
      />


      <div className="relative inline-block w-10 mr-2 align-middle select-none transition duration-200 ease-in">
        <input
          type="checkbox"
          name="toggle"
          id="toggle"
          className="toggle-checkbox absolute block w-6 h-6 rounded-full bg-white border-4 appearance-none cursor-pointer"
        />
        <label
          for="toggle"
          className="toggle-label block overflow-hidden h-6 rounded-full bg-primary cursor-pointer"
        ></label>
      </div>
      <label for="toggle" className="text-primary">
        Privacy
      </label>
    </div>
  );
}
