import React from 'react'
import './Collection.css'
import AltarImage from '../../components/AltarImage/AltarImage'

export default function Collection(props) {
  return (
    <div>
      <h1 className="text-primary mt-44 text-center"> Collection</h1>
      {/* <img src="https://i.imgur.com/zN0kGhO.png" width="500px" alt="placeholder"/> */}
      {props.images.filter((image) => {
        return (image.altar_id === props.altars.id)
      }).map((image) => (
        <>
          <AltarImage user={props.user} image={image}/>

          {/* <img width="100px" src={image.img_url} alt={image.name}/> */}
        </>
      ))}
      <h1 className="text-primary mt-2 mb-4">{props.altars.name}</h1>
    </div>
  )
}
