import React from "react";
import "./AltarForm.css";

export default function AltarForm(props) {
  return (
    <div className="flex ml-8 mt-32 justify-left w-screen">
      <input
        className="rounded-full py-2 px-7 border border-primary text-primary bg-trp bg-opacity-100 focus:ring-2 focus:ring-white"
        type="text"
        name="email"
        placeholder="altar name"
        // value={formData.email}
        // onChange={handleChange}
      />
       <div className="relative">
      {/* <!-- input --> */}
      <input type="checkbox" id="toggleB" className="sr-only" />
      {/* <!-- line --> */}
      <div className="block bg-gray-600 w-14 h-8 rounded-full"></div>
      {/* <!-- dot --> */}
      <div className="dot absolute left-1 top-1 bg-white w-6 h-6 rounded-full transition"></div>
    </div>

    </div>
  );
}
