import React from "react";
import "./AltarForm.css";

export default function AltarForm(props) {
  return (
    <div className="flex ml-8 mt-3 justify-left w-screen">
      <input
        className="rounded-full py-2 px-7 border border-primary text-primary bg-trp bg-opacity-100 focus:ring-2 focus:ring-white"
        type="text"
        name="email"
        placeholder="altar name"
        // value={formData.email}
        // onChange={handleChange}
      />

      <label for="toogleA" class="cursor-pointer">
        <div className="relative">
          <input id="toogleA" type="checkbox" className="sr-only" />
          <div className="w-10 h-4 bg-gray-400 rounded-full shadow-inner"></div>
          <div className="dot absolute w-6 h-6 bg-white rounded-full shadow -left-1 -top-1 transition"></div>
        </div>
        <div className="ml-3 text-primary font-medium">Toggle Me!</div>
      </label>
    </div>
  );
}
